import { Component } from '@angular/core';
import { RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-landing',
  standalone: true,
  imports: [RouterModule, CommonModule],
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.scss']
})
export class LandingComponent {
  title = 'Welcome to Vedic Web';

  // Left menu items (4 items)
  leftMenuItems = [
    {
      title: 'Products',
      submenu: [
        { title: 'Web Development', link: '#' },
        { title: 'Mobile Apps', link: '#' },
        { title: 'Cloud Solutions', link: '#' }
      ]
    },
    {
      title: 'Services',
      submenu: [
        { title: 'Consulting', link: '#' },
        { title: 'Support', link: '#' },
        { title: 'Training', link: '#' }
      ]
    },
    {
      title: 'Solutions',
      submenu: [
        { title: 'Enterprise', link: '#' },
        { title: 'Small Business', link: '#' },
        { title: 'Startups', link: '#' }
      ]
    },
    {
      title: 'Company',
      submenu: [
        { title: 'About Us', link: '#' },
        { title: 'Our Team', link: '#' },
        { title: 'Careers', link: '#' }
      ]
    }
  ];

  // Right menu items (4 items)
  rightMenuItems = [
    {
      title: 'Resources',
      submenu: [
        { title: 'Documentation', link: '#' },
        { title: 'Blog', link: '#' },
        { title: 'Tutorials', link: '#' }
      ]
    },
    {
      title: 'Support',
      submenu: [
        { title: 'Help Center', link: '#' },
        { title: 'Contact Us', link: '#' },
        { title: 'FAQ', link: '#' }
      ]
    },
    {
      title: 'Community',
      submenu: [
        { title: 'Forums', link: '#' },
        { title: 'Events', link: '#' },
        { title: 'Partners', link: '#' }
      ]
    },
    {
      title: 'Account',
      submenu: [
        { title: 'Login', link: '#' },
        { title: 'Register', link: '#' },
        { title: 'Profile', link: '#' }
      ]
    }
  ];
}
